import React, {useState, useEffect} from "react";
import DanceTeam from "./components/DanceTeam";


/*
  {
    id: adfgsdfg,
    name: "TODO Item",
    checked: false,
  }
*/

const TEAM_NAMES_KEY = "team-names";

function uuidv4() {
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, function (c) {
    var r = (Math.random() * 16) | 0,
      v = c == "x" ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

const createDanceTeam = (name) => {
  return {
    id: uuidv4(),
    name: name,
    checked: false,
  };
};

const TeamsApp= () => {
  const [newTeamName, setNewTeamName] = useState("");
  const [teamNames, setTeamNames] = useState([]);

  useEffect(() => {
    if (teamNames.length > 0) {
      localStorage.setItem(TEAM_NAMES_KEY, JSON.stringify(teamNames));
    }
  }, [teamNames]);

  useEffect(() => {
    const fetchedTeamNames = localStorage.getItem(TEAM_NAMES_KEY);
    setTeamNames(JSON.parse(fetchedTeamNames) || []);
  }, []);

  const handleCreateTeam = () => {
    setTeamNames([...teamNames, createDanceTeam(newTeamName)]);
  };

  const handleToggleChecked = (id) => {
    const updatedTeams = teamNames.map((item) => {
      if (item.id !== id) {
        return item;
      }

      return {
        ...item,
        checked: !item.checked,
      };
    });

    setTeamNames(updatedTeams);
  };

  console.log(teamNames);

  return (
    <div>
      <input
        type="text"
        value={newTeamName}
        onChange={(event) => setNewTeamName(event.target.value)}
      />
      <button onClick={handleCreateTeam}>Create Todo</button>

      <h3>Todo Items</h3>
      {teamNames.map((item) => {
        return (
          <DanceTeam
            id={item.id}
            name={item.name}
            checked={item.checked}
            onToggleChecked={handleToggleChecked}
          />
        );
      })}
    </div>
  );
};

export default TeamsApp;
