//         checked: !item.checked,
//       };
//     });

//     setTeamNames(updatedTeams);
//   };

  console.log(teamNames);

  return (
    <div>
      <input
        type="text"
        value={newTeamName}
        onChange={(event) => setNewTeamName(event.target.value)}
      />
      <button onClick={addNewTeam}>Submit Team</button>

      <h3>Dance Teams</h3>
      {teamNames.map((item) => {

        return (
          <DanceTeam
            id={item.id}
            name={item.name}
          />
        );
      })}
    </div>
  );
};

export default TeamsApp;
